
public class Ejercicio01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declarar variables
		int num;
		String nombre;
		
		//Inicializar variables
		num=25;
		nombre="DAM1";
		
		//Mostrar valores
		System.out.println("El valor del número es: " + num + ".");
		
		System.out.println("Estamos en clase de " + nombre);
		
		//Modificar valores
		num=num+35;
		num+=5;
		
		num=num+1;
		num++;
		System.out.println(num+1); //No modifica la variable
		
		//Volver a mostrar el valor del numero
		System.out.println("El valor del número es: " + num + ".");
		
		System.out.println("Hola Mundo!"); //syso Ctrl + Espace + Enter
	}

}
